import React, { useState, useMemo } from "react"

export default function FilteredList({list}) {
    const [users, setUsers] = useState(list)

    function usersFilter() {
        setUsers((user) => user.filter((item) => item.age >= 18))
    }

    useMemo(() => {
        usersFilter(user)
        console.log("sta facendo qualcosa?")
    }, [user])

    function handleOnClick() {
        setUsers((user) => user)
        console.log("sono stato cliccato")
    }

    
    return (<div>
        <button onClick={handleOnClick}>Show list</button>
        {users.map((person) => <h1>Name: {person.name} <br></br> Age: {person.age} <br></br> Id: {person.id}</h1>)}
    </div>)
}
